mapper.update:
UPDATE items
SET
name = #{name},
price = #{price},
stock_quantity = #{stockQuantity}
WHERE id = #{id}

UPDATE items
SET stock_quantity = #{stockQuantity}
WHERE id = #{id}

UPDATE items
SET
stock_quantity = #{stockQuantity},
price = #{price}
WHERE id = #{id}
