mapper.select:
SELECT /* Orders */ o.id AS order_id, o.user_id AS user_id, o.order_date AS order_date, o.total_price AS total_price, o.status AS status,
/* OrderItems */ oi.id AS oi_id, oi.order_id AS oi_order_id, oi.item_id AS oi_item_id, oi.quantity AS quantity,
/* Items */ i.id AS i_item_id, i.name AS item_name, i.price AS item_price, i.stock_quantity AS item_stock
FROM orders o LEFT JOIN order_items oi ON o.id = oi.order_id LEFT JOIN items i ON oi.item_id = i.id WHERE o.id = #{id}

mapper.update:
UPDATE orders
SET status = #{status}
WHERE id = #{orderId}

UPDATE orders
SET
user_id = #{userId},
status = #{status},
total_price = #{totalPrice}
WHERE id = #{id}

UPDATE orders
SET total_price = #{totalPrice}
WHERE id = #{orderId}

update.foreach:
UPDATE order_items
SET
item_id = #{oi.itemId},
quantity = #{oi.quantity}
WHERE
id = #{oi.id}
AND order_id = #{id}

mapper.delete:
DELETE FROM order_items;
DELETE FROM orders;
DELETE FROM items;
DELETE FROM users;
